    private void jButton1calculoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1calculoActionPerformed
        // TODO add your handling code here:

        try {

            paralelepipedo pa = new paralelepipedo();

            pa.setAltura(Float.parseFloat(jTextField1altura.getText()));
            pa.setLargura(Float.parseFloat(jTextField1largura.getText()));
            pa.setComprimento(Float.parseFloat(jTextField2comprimento.getText()));

            String saida = "Dados do Paralelepipedo \n:";

            saida += "Altura: \n" + pa.getAltura() + "\n";
            saida += "Largura: \n" + pa.getLargura() + "\n";
            saida += "Comprimento: \n" + pa.getComprimento() + "\n";
            saida += "√Årea: \n" + pa.calcularArea() + "\n";
            saida += "Volume: \n" + pa.calcularVolume() + "\n";

            jTextArea1resultado.setText(saida);

        } catch (Exception erro) {

            JOptionPane.showMessageDialog(this, erro.getMessage());
        }

    }//GEN-LAST:event_jButton1calculoActionPerformed

